mapper.select:
UPDATE TB_YOUTUBER
   SET YOUTUBER_USERNAME = #{youtuberName}
      ,YOUTUBER_NUMBER = #{youtuberNum}
      ,GRADE_CDOE = #{gradeCode}
 WHERE YOUTUBER_CODE = #{youtuberCode}

mapper.insert:
  	INSERT INTO
  	  TB_YOUTUBER
  	  (
	  	 YOUTUBER_CODE
	  	,YOUTUBER_USERNAME
	  	,YOUTUBER_NUMBER
	  	,YOUTUBER_URL
	  	,GRADE_CODE
	  	,MANAGER_CODE
	  	,DEAL_YN
  	  )
  VALUES 
    (
      YTB_SEQ.NEXTVAL
     ,#{youtuberName}
     ,#{youtuberNum}
     ,#{youtuberUrl}
     ,#{gradeCode}
     ,#{managerCode}
     ,'Y'
    )
